select.if:
SELECT * FROM IMAGE
WHERE BOARD_NO = #{boardNo}
ORDER BY IMAGE_LEVEL

mapper.update:
UPDATE BOARD SET 
BOARD_READ_COUNT = BOARD_READ_COUNT+1 
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT MEMBER.MEMBER_NO, MEMBER_ID, MEMBER_NM, BOARD.BOARD_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_CREATE_DT, BOARD.WORRY_CATEGORY_CD, w.WORRY_NM WORRY_CATEGORY_NM, e.EMPATHY_ARRAY, e.CNT_ARRAY FROM BOARD LEFT JOIN MEMBER ON MEMBER.MEMBER_NO = BOARD.MEMBER_NO LEFT JOIN WORRY_CATEGORY w ON w.worry_category_cd = BOARD.WORRY_CATEGORY_CD LEFT JOIN( SELECT a.BOARD_NO, LISTAGG( a.EMPATHY_STATUS_CD, ',' ) WITHIN GROUP (ORDER BY a.EMPATHY_STATUS_CD) AS EMPATHY_ARRAY, LISTAGG( a.cnt, ',' ) WITHIN GROUP (ORDER BY a.cnt) AS CNT_ARRAY FROM ( SELECT BOARD_NO, EMPATHY_STATUS_CD, COUNT(EMPATHY_STATUS_CD) cnt FROM EMPATHY GROUP BY BOARD_NO, EMPATHY_STATUS_CD ) a GROUP BY BOARD_NO ) e on e.BOARD_NO = BOARD.BOARD_NO WHERE BOARD_CATEGORY_CD = #{boardCategoryCode}
AND BOARD.WORRY_CATEGORY_CD = #{worryCategoryCode}
AND MEMBER_ID LIKE #{val}
AND BOARD_CONTENT LIKE #{val}

SELECT COUNT(*) FROM BOARD
WHERE BOARD_STATUS_CD != 203
AND MEMBER_NO = #{memberNo}
