mapper.select:
SELECT * FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND STATUS_CD = 0

SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_ID = #{inputId}
AND STATUS_CD != 1

SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_FN = #{inputNickNm}
AND STATUS_CD != 1

mapper.update:
UPDATE MEMBER SET
    STATUS_CD = '1'
WHERE MEMBER_NO = #{memberNo}
